style.text:
body { font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; background: linear-gradient(135deg, #1a1a2e 0%, #0f0f1e 100%); margin: 0; padding: 20px; min-height: 100vh; color: #ffffff; }
.tutorial-main { max-width: 1200px; margin: 0 auto; background: #16213e !important; padding: 50px; border-radius: 5px; box-shadow: 0 10px 40px rgba(0,0,0,0.5); border: 1px solid #1f4068; color: #e0e8f0 !important; }
.tutorial-main * { color: #e0e8f0; }
.tutorial-main h1 { color: #4fc3f7 !important; }
.tutorial-main h2 { color: #4fc3f7 !important; }
.tutorial-main h3 { color: #e94560 !important; }
.nav-buttons { display: grid; grid-template-columns: repeat(auto-fit, minmax(250px, 1fr)); gap: 15px; margin: 30px 0; }
.nav-btn { padding: 20px; font-size: 1.1em; background: linear-gradient(135deg, #e94560 0%, #c72e4a 100%); color: white; border: none; border-radius: 3px; cursor: pointer; transition: all 0.3s; font-weight: bold; box-shadow: 0 4px 15px rgba(233,69,96,0.3); }
.nav-btn:hover { transform: translateY(-3px); box-shadow: 0 10px 25px rgba(233,69,96,0.5); background: linear-gradient(135deg, #ff5577 0%, #e94560 100%); }
h1 { color: #4fc3f7; text-align: center; font-size: 3em; margin-bottom: 10px; text-shadow: 0 0 20px rgba(79,195,247,0.4); }
.subtitle { text-align: center; color: #e0e8f0; font-size: 1.2em; margin-bottom: 40px; }
